package com.example.tmap;import android.content.Context;import android.graphics.BitmapFactory;import android.os.Bundle;import android.util.Log;import android.view.Gravity;import android.view.LayoutInflater;import android.view.MotionEvent;import android.view.View;import android.view.ViewGroup;import android.widget.Button;import android.widget.FrameLayout;import android.widget.LinearLayout;import android.widget.TextView;import android.widget.Toast;public class BalloonOverlayView extends FrameLayout {	private final LinearLayout layout;	private Button title;	private TextView subTitle;		private TextView txtContents1;	private TextView txtContents2;		public BalloonOverlayView(Context context, String labelName, String id) {		super(context);		setPadding(10, 0, 10, 0);		layout = new LinearLayout(context);		layout.setVisibility(VISIBLE);		setupView(context, layout, labelName, id);				LayoutParams params = new LayoutParams(								LayoutParams.WRAP_CONTENT, LayoutParams.WRAP_CONTENT);		params.gravity = Gravity.NO_GRAVITY;		addView(layout, params);	}	protected void setupView(Context context, final ViewGroup parent, String labelName, String id) {		LayoutInflater inflater = (LayoutInflater) context.getSystemService(Context.LAYOUT_INFLATER_SERVICE);						View view = inflater.inflate(R.layout.bubble_popup, parent, true);		title = (Button) view.findViewById(R.id.bubble_contents1);		subTitle = view.findViewById(R.id.bubble_contents2);		setTitle(labelName);		setSubTitle(id);	Log.d("44444444",""+		view.isInTouchMode());		view.setOnClickListener(new OnClickListener() {			@Override			public void onClick(View view) {				Log.d("dsaj","sdsdfhgdsfgdgfdcggdf");			}		});	Log.d("123",""+view.hasOnClickListeners());	}	public void setTitle(String str) {		title.setText(str);	}		public void setSubTitle(String str) {		subTitle.setText(str);	}	public void setTxtContents1(String strContents) {		txtContents1.setText(strContents);	}	public void setTxtContents2(String strContents) {		txtContents2.setText(strContents);	}}